mapper.update:
UPDATE screencontent
screen_date = TO_DATE(#{screenDate}, 'YYYY/MM/DD'), screen_opentime = TO_DATE(#{screenOpenTime},'YYYY/MM/DD HH24:MI'), screen_endtime = TO_DATE(#{screenEndTime},'YYYY/MM/DD HH24:MI'), screen_theater = #{screenTheater}, ticket_price = #{ticketPrice}, preview_flag = #{previewFlag}, preview_title = #{previewTitle}, ticket_opendate = TO_DATE(#{ticketOpenDate}, 'YYYY/MM/DD HH24:MI:SS'), invite_actor = #{inviteActor} preview_flag = #{previewFlag}, preview_title = NULL, ticket_opendate = NULL, invite_actor = NULL
WHERE screencontent_no = #{screenContentNo}

mapper.select:
SELECT screencontent_no
FROM screencontent 
WHERE movie_no=#{movie.movieNo} 

AND screencontent_no != #{screenContentNo} 

AND preview_Flag = 'Y'

SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT movie.movie_title, movie.director, movie.poster, movie.movie_no,screencontent.screencontent_no, screencontent.invite_actor, screencontent.preview_title, TO_CHAR(screencontent.screen_opentime,'YYYY-MM-DD') screen_opentime , screencontent.ticket_price, TO_CHAR(screenContent.ticket_opendate,'YYYY-MM-DD') ticket_opendate FROM movie, (SELECT * FROM screencontent WHERE screen_opentime> SYSDATE AND preview_flag = 'Y') screencontent WHERE movie.movie_no = screencontent.movie_no
AND screencontent.preview_title LIKE '%'||#{searchKeyword}||'%'
ORDER BY movie_no ) inner_table WHERE ROWNUM <= #{endRowNum}) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT movie.movie_title, movie.director, movie.poster, movie.movie_no,screencontent.screencontent_no, screencontent.invite_actor, screencontent.preview_title, TO_CHAR(screencontent.screen_opentime,'YYYY/MM/DD HH24:MI') screen_opentime , screencontent.ticket_price, TO_CHAR(screenContent.ticket_opendate,'YYYY/MM/DD HH24:MI') ticket_opendate FROM movie, (SELECT * FROM screencontent WHERE screen_opentime> SYSDATE AND preview_flag = 'Y') screencontent WHERE movie.movie_no = screencontent.movie_no
AND screencontent.preview_title LIKE '%'||#{searchKeyword}||'%'
ORDER BY movie_no ) inner_table WHERE ROWNUM <= #{endRowNum}) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
